package hanjia;

public class h13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int  i =13;
		float f=6235.748f;
		System.out.println("f+i :"+n(i,f));
		System.out.println("f/i :"+g(i,f));
	}
	public static float n(int i,float f){
		
		return (f+i);
	}
	public static float g(int i,float f) {
		
		return (f/i);
	}

}
